import React from 'react';

function GameOverScreen({ gameState, onPlayAgain }) {
    return (
        <div className="game-over-screen card">
            <h2>Game Over!</h2>
            <h1>ðŸŽ‰ Winner: {gameState.gameWinner} ðŸŽ‰</h1>
            
            <div className="final-scores">
                {gameState.players.map(p => (
                    <div key={p.id}>
                        <h4>{p.name}</h4>
                        <p>Final Score: {p.totalScore}</p>
                        <p>Rounds Won: {p.roundsWon}</p>
                    </div>
                ))}
            </div>

            <button onClick={onPlayAgain} className="btn btn-primary">Play Again</button>
        </div>
    );
}

export default GameOverScreen;
