import React, { useState } from 'react';

function WelcomeScreen({ playerName, setPlayerName, onCreateGame, onJoinGame, waitingMessage }) {
    const [joinCode, setJoinCode] = useState('');

    return (
        <div className="welcome-screen card">
            <h2>Welcome to Words Collide!</h2>
            {waitingMessage ? (
                <div className="waiting-message">
                    <h3>{waitingMessage}</h3>
                    <div className="loader"></div>
                </div>
            ) : (
                <>
                    <input
                        type="text"
                        value={playerName}
                        onChange={(e) => setPlayerName(e.target.value)}
                        placeholder="Enter your name"
                        className="input-field"
                    />
                    <button onClick={onCreateGame} className="btn btn-primary">Create Game</button>
                    <div className="join-game">
                        <input
                            type="text"
                            value={joinCode}
                            onChange={(e) => setJoinCode(e.target.value.toUpperCase())}
                            placeholder="Enter game code"
                            maxLength="4"
                            className="input-field"
                        />
                        <button onClick={() => onJoinGame(joinCode)} className="btn btn-secondary">Join Game</button>
                    </div>
                </>
            )}
        </div>
    );
}

export default WelcomeScreen;
